DECLARE @MinDate DATE = '2020-01-01',
        @MaxDate DATE = '2020-08-04';

SELECT fechaCorte, MONTH(fechaCorte) as mesCorte, YEAR(fechaCorte) as anioCorte
FROM
(
	SELECT  
	TOP (DATEDIFF(MONTH, @MinDate, @MaxDate) + 1)
	fechaCorte = EOMONTH(DATEADD(MONTH, ROW_NUMBER() OVER(ORDER BY a.object_id) - 1, @MinDate))
	FROM    sys.all_objects a 
	CROSS JOIN sys.all_objects b
)fechas

ORDER BY fechaCorte;
